import React from 'react';
import { TrendingUp, Users, Globe } from 'lucide-react';

const Hero = () => {
  return (
    <section style={{
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 30%, #334155 100%)',
      color: 'white',
      padding: '120px 0 80px',
      textAlign: 'center',
      position: 'relative',
      overflow: 'hidden'
    }}>
      <div className="container">
        <h1 style={{
          fontSize: '3.5rem',
          fontWeight: '700',
          marginBottom: '1.5rem',
          lineHeight: '1.2'
        }}>
          Kinh Tế Thị Trường Định Hướng<br />
          <span style={{ color: '#fbbf24' }}>Xã Hội Chủ Nghĩa Ở Việt Nam</span>
        </h1>

        <p style={{
          fontSize: '1.25rem',
          marginBottom: '3rem',
          maxWidth: '800px',
          margin: '0 auto 3rem',
          opacity: 0.9
        }}>
          Hiểu rõ mô hình kinh tế độc đáo của Việt Nam: Sự kết hợp biện chứng giữa hiệu quả thị trường và công bằng xã hội
        </p>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
          gap: '2rem',
          marginTop: '4rem'
        }}>
          <div style={{
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
            padding: '2rem',
            borderRadius: '12px',
            backdropFilter: 'blur(10px)'
          }}>
            <TrendingUp size={48} style={{ color: '#fbbf24', marginBottom: '1rem' }} />
            <h3 style={{ fontSize: '1.25rem', fontWeight: '600', marginBottom: '0.5rem' }}>Hiệu Quả Kinh Tế</h3>
            <p style={{ opacity: 0.8 }}>Phát triển lực lượng sản xuất và tăng trưởng bền vững</p>
          </div>

          <div style={{
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
            padding: '2rem',
            borderRadius: '12px',
            backdropFilter: 'blur(10px)'
          }}>
            <Users size={48} style={{ color: '#fbbf24', marginBottom: '1rem' }} />
            <h3 style={{ fontSize: '1.25rem', fontWeight: '600', marginBottom: '0.5rem' }}>Công Bằng Xã Hội</h3>
            <p style={{ opacity: 0.8 }}>Đảm bảo phúc lợi và an sinh cho mọi người dân</p>
          </div>

          <div style={{
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
            padding: '2rem',
            borderRadius: '12px',
            backdropFilter: 'blur(10px)'
          }}>
            <Globe size={48} style={{ color: '#fbbf24', marginBottom: '1rem' }} />
            <h3 style={{ fontSize: '1.25rem', fontWeight: '600', marginBottom: '0.5rem' }}>Hội Nhập Quốc Tế</h3>
            <p style={{ opacity: 0.8 }}>Mở cửa và tham gia chuỗi giá trị toàn cầu</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
